mapper.select:
/*장비 구분 테이블*/
SELECT EQPMN_ID /*장비구분ID */ ,EQPMN_NM /*장비구분명*/ ,UPPER_EQPMN_ID /*상위장비구분ID*/ ,FRST_REGIST_DT /*최초등록일시*/ ,FRST_REGISTER_SN /*최초등록자 일련번호*/ ,UPDT_DT /*수정일시*/ ,UPDUSR_SN /*수정자 일련번호*/ ,EQPMN_OBJ /*물품인지 계층인지 구분*/
FROM KJP_EQPMN_CODE
EQPMN_NM LIKE '%' || #{eqpmnNm} || '%'
AND EQPMN_ID = #{eqpmnId}
AND UPPER_EQPMN_ID = #{upperEqpmnId}

mapper.insert:
INSERT INTO KJP_EQPMN_CODE(
			 EQPMN_ID           /*장비구분ID */
			,EQPMN_NM         /*장비구분명*/
			,UPPER_EQPMN_ID    /*상위장비구분ID*/
			,FRST_REGIST_DT    /*최초등록일시*/
			,FRST_REGISTER_SN  /*최초등록자 일련번호*/
			,EQPMN_OBJ)

		VALUES(
			 #{eqpmnId}
			,#{eqpmnNm}
			,#{upperEqpmnId}
			,sysdate
			,1
			,#{eqpmnObj}
		)
