mapper.select:
/*장비 테이블*/
SELECT EQPMN_SN /*장비 일련번호(1부터 생성)*/ ,EQPMN_SE_CODE /*장비 구분코드*/ ,EQPMN_NM /*장비 장비명*/ ,ORGNZT_ID /*소속부서ID*/ ,BUY_DAY /*구매일자*/ ,EQPMN_UNIT_PRICE /*단가*/ ,EQPMN_MODEL_NAME /*모델명*/ ,EQPMN_MAKR /*제조사*/ ,EQPMN_PIN /*식별번호*/ ,EQPMN_CN /*비고*/ FROM KJP_EQPMN
EQPMN_NM LIKE '%' || #{eqpmnNm} || '%'
EQPMN_SE_CODE LIKE '%' || #{eqpmnSeCode} || '%'
AND EQPMN_SN = #{eqpmnSn}

mapper.insert:
			/*장비  테이블 입력*/
			INSERT INTO KJP_EQPMN(
				 EQPMN_SN 						/*장비 일련번호(1부터 생성)*/
				 ,EQPMN_SE_CODE					/*장비 구분코드*/
				 ,EQPMN_NM						/*장비 장비명*/	
				 ,ORGNZT_ID						/*소속부서ID*/

  			  	 ,BUY_DAY 						/*구매일자*/


  			  	 ,EQPMN_UNIT_PRICE 				/*단가*/	


  			  	 ,EQPMN_MODEL_NAME 				/*모델명*/


  			  	 ,EQPMN_MAKR 					/*제조사*/	

				 ,EQPMN_PIN						/*식별번호*/

  			  	 ,EQPMN_CN 						/*비고*/

				 ,FRST_REGIST_DT				/*최초등록일시*/
				 ,FRST_REGISTER_SN)				/*수정자 일련번호*/
		values( (SELECT NVL(MAX(EQPMN_SN),0)+1 FROM KJP_EQPMN)
				,#{eqpmnSeCode}
				,#{eqpmnNm}
				,#{orgnztId}

  			  	 ,#{buyDay}


  			  	,#{eqpmnUnitPrice}


  			  	,#{eqpmnModelName}


  			  	 ,#{eqpmnMakr}

				,#{eqpmnPin}

  			  	,#{eqpmnCn}

				,SYSDATE
				,1
		)
