mapper.select:
/* ID중복체크 SELECT Query */
SELECT COUNT(1) FROM KJP_USER
	WHERE USER_ID = #{userId}	/* User 아이디 */

mapper.insert:
/* 회원가입 INSERT Query */
	INSERT INTO KJP_USER 
		(USER_SN 				/*User 일련번호 */
		,USER_ID 				/*User 아이디 */
		,USER_PW 				/*User 패스워드 */
		,USER_NM 				/*User 이름 */
		,DT_BRT 				/*User 생년월일 */
		,USER_GNDR 				/*User 성별 */
		,MOBLPHON_NO 			/*User 전화번호 */
		,EMAIL_ADRES 			/*User 이메일주소 */
		,FRST_REGISTER_SN) 		/*최초 등록자 일련번호(회원가입 시, 본인 User 일련번호) */
	VALUES(
			(SELECT NVL(MAX(USER_SN) , 0) + 1 FROM KJP_USER)
			,#{userId}
			,#{userPw}
			,#{userNm}
			,#{userBrt}
			,#{userGndr}
			,#{userPhon}
			,#{userEmail}
			,(SELECT NVL(MAX(USER_SN) , 0) + 1 FROM KJP_USER)
			)

mapper.select:
/* 로그인시 ID,PW 확인하는 Query */
	SELECT  
			USER_ID 		/*User 아이디 */
			,USER_PW 		/*User 패스워드 */
		FROM KJP_USER
		WHERE USER_ID=#{userId}
